private void btnCalcularMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_btnCalcularMouseClicked
    int num1,num2,total=0;
    num1=Integer.parseInt(this.txtNum1.getText());
    num2=Integer.parseInt(this.txtNum2.getText());
    if(this.jComboBox1.getSelectedIndex()==0){
        total=num1+num2;
    }else if(this.jComboBox1.getSelectedIndex()==1){
        total=num1-num2;        
    }else if(this.jComboBox1.getSelectedIndex()==2){
        total=num1*num2;        
    }else if(this.jComboBox1.getSelectedIndex()==3){
        total=num1/num2;        
    }
    this.txtTotal.setText(String.valueOf(total));
}//GEN-LAST:event_btnCalcularMouseClicked

